let toggleFriend=function(e){e.click((function(r){r.preventDefault(),$.ajax({method:"get",url:e.attr("href"),success:function(r){r.data.friendsDeleted?e.text("Add Friend"):e.text("Remove Friend")},error:function(e){console.log(e)}})}))};toggleFriend($("#toggleFriend"));let removeFriend=function(e){e.click((function(r){r.preventDefault(),$.ajax({method:"get",url:e.attr("href"),success:function(e){$(`#friend-${e.data.friendShipId}`).remove()},error:function(e){console.log(e)}})}))},userFriends=$(".remove-friend");for(let e=0;e<userFriends.length;e++)removeFriend(userFriends.eq(e));